// SPDX-License-Identifier: MIT

pragma solidity ^0.8.7;

import "@chainlink/contracts/src/v0.8/interfaces/LinkTokenInterface.sol";
import "@chainlink/contracts/src/v0.8/interfaces/VRFCoordinatorV2Interface.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBaseV2.sol";

error Game_SendMoreToEnterGame();
error Game_TransferFailed();

contract handGame is VRFConsumerBaseV2 {
    //1. Player enters the game paying a fee
    //2. Computer picks a random type out of 3 (Rock, paper or Scissor) using VRF
    //3. Player picks a type
    //4. Compare computer and player type and select winner based on Rock, paper, Scissor winning pairs

    VRFCoordinatorV2Interface immutable COORDINATOR;
    LinkTokenInterface immutable LINKTOKEN;

    // Your subscription ID.
    uint64 immutable s_subscriptionId;

    // The gas lane to use, which specifies the maximum gas price to bump to.
    // For a list of available gas lanes on each network,
    // see https://docs.chain.link/docs/vrf-contracts/#configurations
    bytes32 immutable s_keyHash;

    // Depends on the number of requested values that you want sent to the
    // fulfillRandomWords() function. Storing each word costs about 20,000 gas,
    // so 100,000 is a safe default for this example contract. Test and adjust
    // this limit based on the network that you select, the size of the request,
    // and the processing of the callback request in the fulfillRandomWords()
    // function.
    uint32 immutable s_callbackGasLimit = 100000;

    // The default is 3, but you can set this higher.
    uint16 immutable s_requestConfirmations = 3;

    // For this example, retrieve 2 random values in one request.
    // Cannot exceed VRFCoordinatorV2.MAX_NUM_WORDS.
    uint32 immutable s_numWords = 1;

    uint256[] public s_randomWords;
    uint256 public s_requestId;
    address s_owner;

    // Game variables

    enum Hand {
        ROCK,
        PAPER,
        SCISSORS
    }
    uint256 private i_entranceFee;
    address payable[] private s_players;
    address payable public winner;
    mapping(address => Hand) private playerPick;
    mapping(address => uint256) private addressToAmountFunded;
    mapping(uint256 => address) private requestIdToPlayer;
    Hand public computerHand;

    // Events

    event GameEntry(address indexed player);

    event ReturnedRandomness(uint256[] randomWords);

    constructor(
        uint64 subscriptionId,
        address vrfCoordinator,
        address link,
        bytes32 keyHash,
        uint256 entranceFee
    ) VRFConsumerBaseV2(vrfCoordinator) {
        COORDINATOR = VRFCoordinatorV2Interface(vrfCoordinator);
        LINKTOKEN = LinkTokenInterface(link);
        s_keyHash = keyHash;
        s_owner = msg.sender;
        s_subscriptionId = subscriptionId;
        i_entranceFee = entranceFee;
    }

    /**
     * @notice Requests randomness
     * Assumes the subscription is funded sufficiently; "Words" refers to unit of data in Computer Science
     */

    function enterGame() public payable {
        if (msg.value < i_entranceFee) {
            revert Game_SendMoreToEnterGame();
        }
        s_players.push(payable(msg.sender));
        addressToAmountFunded[msg.sender] = msg.value;
        emit GameEntry(msg.sender);
    }

    function pickType(uint256 _hand) external returns (uint256) {
        require(addressToAmountFunded[msg.sender] == i_entranceFee);
        Hand hand = Hand(_hand);
        playerPick[msg.sender] = hand;
        s_requestId = COORDINATOR.requestRandomWords(
            s_keyHash,
            s_subscriptionId,
            s_requestConfirmations,
            s_callbackGasLimit,
            s_numWords
        );
        requestIdToPlayer[s_requestId] = msg.sender;
    }

    /**
     * @notice Callback function used by VRF Coordinator
     *
     * @param requestId - id of the request
     * @param randomWords - array of random results from VRF Coordinator
     */
    function fulfillRandomWords(uint256 requestId, uint256[] memory randomWords)
        internal
        override
    {
        s_randomWords = randomWords;
        // Computer hand calculated
        computerHand = Hand(s_randomWords[0] % 3);

        emit ReturnedRandomness(randomWords);

        // Check who is the winner
        // Rock wins scissors (1 wins 3)
        // Paper wins rock (2 wins 1)
        // Scissors wins paper (3 wins 2)

        if (playerPick[requestIdToPlayer[requestId]] == Hand.ROCK) {
            if (computerHand == Hand.SCISSORS) {
                winner = payable(requestIdToPlayer[requestId]);
                (bool success, ) = winner.call{
                    value: address(this).balance - i_entranceFee
                }("");
                // require(success, "Transfer failed");
                if (!success) {
                    revert Game_TransferFailed();
                }
            }
        }
        if (playerPick[requestIdToPlayer[requestId]] == Hand.PAPER) {
            if (computerHand == Hand.ROCK) {
                winner = payable(requestIdToPlayer[requestId]);
                (bool success, ) = winner.call{
                    value: address(this).balance - i_entranceFee
                }("");
                // require(success, "Transfer failed");
                if (!success) {
                    revert Game_TransferFailed();
                }
            }
        }
        if (playerPick[requestIdToPlayer[requestId]] == Hand.SCISSORS) {
            if (computerHand == Hand.PAPER) {
                winner = payable(requestIdToPlayer[requestId]);
                (bool success, ) = winner.call{
                    value: address(this).balance - i_entranceFee
                }("");
                // require(success, "Transfer failed");
                if (!success) {
                    revert Game_TransferFailed();
                }
            }
        } else {
            winner = payable(address(this));
        }
    }

    modifier onlyOwner() {
        require(msg.sender == s_owner);
        _;
    }
}
